float circle( vec2 frag, vec2 origin, float radius ) {
    return distance(frag, origin) - radius;
}

float smin( float a, float b, float k )
{
    float res = exp( -k*a ) + exp( -k*b );
    return -log( res )/k;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 fragPos = (fragCoord / iResolution.xy) - 0.5;
    fragPos.x *= iResolution.x / iResolution.y;
    fragPos *= 2.0;

    vec2 origin = vec2(sin(iTime * 0.7) * 0.72, 0.0);

    float a = circle(fragPos, origin, 0.5);
    float b = circle(fragPos, -origin, 0.5);
    float m = smin(a, b, 5.0);

	//fragColor = vec4(m <= 0.0 ? 1.0 : 0.0);
    fragColor = vec4(1.0 - smoothstep(0.095, 0.1, m));
}
