/** // 255 chars by coyote

#define A atan(U.y,U.x)
void mainImage( out vec4 O,  vec2 U )
{
    U = (U+U-(O.xy=iResolution.xy))/O.y;
    U.y += .1;

    float p = .1592,                                          // 3 symmetries
          a = floor(3.*A*p-p)/3./p +.05,
          c=cos(a);

    c = length(U= 3.*(U*mat2(c,a=sin(a),-a,c) - vec2(0,.577))); // spiral
	O = (.5+.5*sin(A+c/p)) * vec4( c + fract((A+2.25)*p) < 2.);
