float sampleHexagon(vec2 uv)
{
    const float radius = 0.1;
    float width = 2.0 / iResolution.y;

    vec2 U = abs(mod(uv, radius * vec2(1.732, 3.0)) - radius * vec2(0.866, 1.5));
    float d = U.x - max(-width, 1.732 * min(0.5 * radius, radius - U.y));
    return smoothstep(0.0, width, abs(d - width * 0.5));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.y;
    fragColor = vec4(sampleHexagon(uv));
}
