vec4 color = vec4(1.0, 1.0, 1.0, 1.0);
float size = 32.0f;
float modSize = 2.0f; //Also affects the size of the diamonds
float modSize2 = 2.0f;
bool red = true;
bool green = true;
bool blue = true;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    if (red)
    	color.r += cos(iTime) * sin(iTime);

    if (green)
    	color.g += cos(iTime) * sin(iTime);

    if (blue)
    	color.b += cos(iTime) * sin(iTime);

    vec2 point = fragCoord / size;
	float pat = mod(point.x + point.y, modSize);
	float pat2 = mod(point.y - point.x, modSize2);
    fragColor = pat * pat2 * color; 
}
