#define float3 vec3
#define float2 vec2
#define float4 vec4

float XOR(float x, float y)
{
    return x+y-2.0*x*y;
}    

#define AA 0.5

float CV(float3 c, float2 uv)
{
	return 1.0-clamp(AA*(length(c.xy-uv)-c.z),0.,1.);
}

void mainImage(out float4 O, in float2 I)
{
    O=float4(0,0,0,1);
    for(float i=0.;i<10.;i+=.3)
		O.g=XOR(O.g,CV(float3(
			sin(iTime*1.5+i/4.)*(iResolution.x/2.-60.)+iResolution.x/2.,
			sin(iTime*1.3+i/3.)*(iResolution.y/2.-60.)+iResolution.y/2.,
			i*6.0),I));
    O.b=O.g*0.5;
}
