void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy - .5;

    	float width = (1.+cos(iTime/20.));
    	//float width = 0.01;
    	vec2 center = vec2(0.761574, 0.0847596);

    	uv = uv*width-center;


   vec2 z = vec2(0.);

   float i, imax =1000.;
   for (i=0.;i<imax;i++) {
		z = mat2(z, -z.y,z.x) * z + uv;
       if (dot(z,z) > 4.) break;
   }
    // Output to screen
    i = 1.-i*4./(imax);
    vec4 col =  vec4(.6 + .6 * cos( 6.3* i/10.  + vec4(0,23,21,0)  ));
    fragColor = col;
}
