float max3 (vec3 v) {
  return max (max (v.x, v.y), v.z);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec2 myMouse = vec2( mix(0.1, 1.0, iMouse.x), mix(0.1, 1.0, iMouse.y));

    // zoom
    vec2 pos = smoothstep(.1, 1e5, myMouse.xy);

    float zoom = iMouse.x / 100.;

    uv *= zoom;
    uv += smoothstep(0.1, 1.0, length(pos.xy) * 100.0) * zoom;

    // display texture pixels
    vec4 img = texture(iChannel0, uv);

    // turn gray
    float grayF = max3(img.rgb);
    vec3 gray = vec3(grayF);

    // displace
    vec2 posDis = vec2(grayF, grayF);
    vec4 imgDis = texture(iChannel0, (img.xy * (sin(iTime / 10.0) + .5) - (pos.y * 1e5)));
    vec3 col = imgDis.rgb;


    // Output to screen
    fragColor = vec4(col,1.0);
}
