void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //Parameters
    const float waveFrequency = 3.0;
    const float waveAmplitude = 0.03;

    //Constants
	vec2 baseUV = fragCoord.xy / iResolution.xy;
    baseUV.y = -baseUV.y;

    //Main distortion   (wavyness)
    float time1 = iTime * 0.6;
	vec2 mainUV = baseUV;
	mainUV.x += sin(time1 + mainUV.y * waveFrequency) * waveAmplitude;
	mainUV.y += sin(time1 + mainUV.x * waveFrequency) * waveAmplitude;


    //Circle mask
    float maskRadiusPx = iResolution.y * 1.0;
    float distanceToMouse = length(iMouse.xy - fragCoord.xy);
	float distortionParameter = distanceToMouse / maskRadiusPx;
    float distortionIntensity = pow(distortionParameter, 2.0);
    distortionIntensity = clamp(distortionIntensity, 0.0, 1.0);

    //Composition
    vec2 differenceInUV = mainUV - baseUV;

	vec4 surfaceColor = texture(iChannel0, baseUV + differenceInUV * distortionIntensity);

    //fragColor.xyz = vec3(distortionIntensity, 0, 0);
	fragColor.xyz = surfaceColor.xyz;
	fragColor.w = 1.0;
}
