void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

	float pos = iTime/50.0;
	vec2 position = ( fragCoord.xy / iResolution.xy )-pos;

    float phase1 = position.x*15.0+iTime*1.0;
    float phase2 = position.x*25.0;
    float phase3 = iTime*2.0;

	float wave = sin(phase1)*0.05*sin(phase3) + sin(phase2)*0.01*sin(phase3);

    vec3 color1 = vec3(0.933, 0.4, 0.145);
    vec3 color2 = vec3(0.752, 0.098, 0.537);

    vec3 color3 = vec3(1.0, 0.694, 0.592);
    vec3 color4 = vec3(1.0, 0.556, 0.850);

    vec4 texColor1 = texture(iChannel0, ( fragCoord.xy / iResolution.xy )-vec2(iTime/50.0, wave*0.2+iTime/50.0));
    vec4 texColor2 = texture(iChannel0, ( fragCoord.xy / iResolution.xy )-vec2(-iTime/50.0, wave*0.4+iTime/25.0));

    fragColor = vec4( position.y < wave ? mix(color1, color2, min(1.0, pow((fragCoord.y / iResolution.y)/pos, 2.0))) : vec3(0.0,0.0,0.0), 1 );

    if (position.y < wave) {
        if (position.y > wave*1.2 || position.y > wave*0.8 || position.y > wave-0.005) {
            fragColor = vec4( mix(color3, color4, sin(fragCoord.x/100.0+iTime)+1.0), 1.0);
        }

        if (position.y  < wave-0.02 && wave > 0.0) {
            fragColor = mix(fragColor, vec4(1.0, 1.0, 1.0, 1.0), abs(wave*20.0)*((fragCoord.y / iResolution.y)/2.0));
            //fragColor *= position.y/20.0;
        }

        fragColor = mix(fragColor, texColor1, texColor1.r);
        fragColor = mix(fragColor, texColor2, texColor2.b);
    }
}
