vec3 mandlebrot(vec2 c) {
    vec2 z = vec2(0.0);
    for(int i = 0; i < 256; i++) {
        z = vec2(z.x * z.x - z.y * z.y, 2.0 * z.x * z.y) + c;
        if(dot(z, z) > 2.0) {
            float n = float(i) / 20.0;
            return vec3(sin(n), cos(n), tan(n));
		}
    }
}

vec3 supersample(vec2 fc, float size) {
    vec3 sum;

    for(float x = 0.0; x < size; x++) {
        for(float y = 0.0; y < size; y++) {
            vec2 fragCoord = fc + (vec2(x, y) / size) - 0.5;
            vec2 uv = (fragCoord.xy / iResolution.xy * 2.0 - 1.0) * vec2(iResolution.x / iResolution.y, 1.0) + vec2(-0.5, 0.0);
            sum += mandlebrot(uv);
        }
    }

    return sum / pow(size, 2.0);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
	fragColor = vec4(supersample(fragCoord, 4.0), 1.0);
}
