float circle(vec2 uv, vec2 position, float radius, float blur)
{
    float dist = length(uv - position);
    float color = smoothstep(radius, radius - blur, dist);

    return color;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy; // 0 <> 1
    uv -= .5; // -0.5 <> 0.5
    uv.x *= (iResolution.x / iResolution.y); // aspect ratio

    float mask = circle(uv, vec2(0., 0.), .5, 0.01);
    mask -= circle(uv, vec2(.08, 0.0), .35, .035);
    mask += circle(uv, vec2(.1, 0.11), .135, .015); // top part of 8
    mask += circle(uv, vec2(.1, -.14), .15, .015); // bot part of 8
    mask -= circle(uv, vec2(.1, 0.11), .07, .01); // top part of 8
    mask -= circle(uv, vec2(.1, -.14), .08, .01); // bot part of 8

    fragColor = vec4(vec3(	mask), 1.);
}
