void mainImage( out vec4 f, in vec2 w )
{
    vec2 p = -1.+ 2.*w / iResolution.xy;
    float t = iTime, an = t*.25;
    vec2 r = vec2( p.x*cos(an)-p.y*sin(an), p.x*sin(an)+p.y*cos(an)),
    uv = vec2(.25*r.x/abs(r.y), 0.5*t + .01/abs(r.y));
    vec4 c = texture(iChannel0,uv),
    c2 = texture(iChannel1,uv*.01);
    f = c*c2;
}
