void mainImage( out vec4 f, in vec2 w ) {
    vec4 p = vec4(w,0.,1.)/iResolution.xyxy-.5, d=p, t, c;
    p.z += iTime*2.;
    for(float i = 2.; i >0.; i-=.01)
    {
        t = mod(p, 8.)-4.;
        c = texture(iChannel0, abs(t.xyz)+1.);
        t = min(length(t.xy), length(t.xz))-1.2+c;
        f = c*i+i*.1;
        if(t.x<.01) break;
        p -= d*t.x*.2;
     }
}
