#define width 9.0
//#define grayscale

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 out_colour;
    vec2 normal_pos = vec2(1.0, -1.0) * fragCoord.xy/iResolution.xy + vec2(0.0, 1.0);
    vec3 col = pow(texture(iChannel0, normal_pos).xyz,vec3(2.2));

    #ifdef grayscale
    float final = dot(vec3(0.2126, 0.7152, 0.0722), col);
    col = vec3(final);
    #endif

    float c = mod(float(fragCoord.x+fragCoord.y), width);
    out_colour = clamp(col * width - c, vec3(0.0), vec3(1.0));
	fragColor = vec4(pow(out_colour, vec3(1.0/2.2)), 1.0);
}
