void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy/iResolution.xy; // iResolution.xy;
    uv.y = 1.0 - uv.y;
    float timer = mod(iTime,2.0);
    vec3 texture = texture(iChannel0,uv).xyz;
    if(timer>1.0){
    	float greyscaler = dot(texture.xyz,vec3(1.0/3.0));
    	texture = vec3(greyscaler);
    }else{
    }
    fragColor = vec4(texture,1.0);
}
