void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 olduv = uv;
    float asp = iResolution.y / iResolution.x;

    uv.x = floor(uv.x * 200.0) / 200.0;
    uv.y = floor(uv.y * 200.0 * asp) / (200.0 * asp);

    float dc = sqrt(pow(uv.x - 0.5, 2.0) + pow(uv.y - 0.5, 2.0));

    float angle = 2.0 * 3.14 * iTime;
    float mag = 0.04 * dc;
    float ox = mag * cos(angle);
    float oy = mag * sin(angle);
    uv.x += ox;
    uv.y += oy;
    float r = texture(iChannel1, uv).r;
    r = r * r;
    r *= dc * 3.0;

    if (int(floor(olduv.x * 100.0)) % 2 == int(floor(olduv.y * 100.0 * asp)) % 2 && r == 0.0) {
    	r = 0.2;   
    }

	fragColor.r = r;

}
