void circle(vec2 r, vec2 center, float radius, vec3 color, inout vec3 pixel) {
	if( length(r-center) < radius) {
		pixel = color;
	}
}
void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv =  2.0*vec2(gl_FragCoord.xy - 0.5*iResolution.xy)/iResolution.y;
    vec3 backgroundColor = vec3(1.0);
	vec3 color1 = vec3(105,210,231)/255.0;
    vec3 color2 = vec3(167,219,216)/255.0;
    vec3 color3 = vec3(224,228,204)/255.0;

    vec3 pixel = backgroundColor;
    float radius1 = 0.4;
    circle(uv,vec2(0,0),radius1*abs(cos(iTime)),color1,pixel);

    float radius2 = 0.2;
    circle(uv,vec2(0,0),radius2*abs(tan(iTime)),color2,pixel);

    float radius3 = 0.24;
    vec2 circlePos = vec2(0.3,0.4)*sin(iTime);
    circle(uv,circlePos,radius3*abs(sin(iTime)),color3,pixel);


    fragColor = vec4(pixel,1.0);
}
