bool isInCircle(vec2 pixelCoords)
{
    vec2 circleCenter = vec2(0);
    float radius = 0.35 * sin(iTime*2.);
    float distanceToCenter = distance(pixelCoords, circleCenter); 

    bool isInCircle = (distanceToCenter < radius);
    return isInCircle;
}

bool isInLeftPart(vec2 pixelCoords)
{
    bool isInLeftPart = pixelCoords.x < 0.0;
    return isInLeftPart;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy; // 0 <> 1
    uv.xy -= 0.5; // -0.5 <> 0.5
    float aspect = iResolution.x / iResolution.y; // 640 / 360 = 1.78
    uv.x *= aspect;


    if(isInCircle(uv))
    {
        fragColor = vec4(uv.y + 0.5, uv.x + 0.4, 0.3, 1.0);        
    }
    else if(isInLeftPart(uv))
    {
        fragColor = vec4(uv + 0.6,0.5+0.5*sin(iTime),1.0);
    }
    else
    {
        fragColor = vec4(-uv.x + 0.6, uv.y + 0.6, 0.5+0.5*sin(iTime),1.0);
    }
}
