bool inTheBounds(vec2 A, vec2 B, float x)
{
    float minX = min(A.x, B.x);
    float maxX = max(A.x, B.x);

    return minX < x && maxX > x;
}

bool isOnTheSlope(vec2 A, vec2 B, float x, float y)
{
    float slope = (A.y - B.y) / (A.x - B.x);

    float left = y - (slope * x);
    float right = A.y - (slope * A.x);

    return round(left*100.0) == round(right*100.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragCoord = (2.*fragCoord - iResolution.xy) / iResolution.y;

    float x = fragCoord.x;
    float y = fragCoord.y;

	vec3 backgroundColor = vec3(1.0);
	vec3 color1 = vec3(0.216, 0.471, 0.698);
	vec3 color2 = vec3(1.00, 0.329, 0.298);
	vec3 color3 = vec3(0.867, 0.910, 0.247);

    vec3 pixel = backgroundColor;

    vec2 A = vec2(-0.33, -0.66);
    vec2 B = vec2(0.33, 0.66);
    vec2 C = vec2(0.66, -0.33);

    if(inTheBounds(A, B, x) && isOnTheSlope(A, B, x, y) )
    {
        pixel = color1;
    }

    if(inTheBounds(B, C, x) && isOnTheSlope(B, C, x, y) )
    {
        pixel = color2;
    }

    if(inTheBounds(C, A, x) && isOnTheSlope(C, A, x, y) )
    {
        pixel = color3;
    }

    fragColor = vec4(pixel, 1.0);
}
