void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    uv = uv * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;

    float angle = iTime / 2.0;
    float light = sin(angle);
    if (light < 0.0) light = 0.0;

    // sky
	fragColor = vec4(uv.y * light, uv.y * light, light, 1.0);

    // sun
    vec2 sun = vec2(cos(angle) * 1.9, 1.5 * sin(angle) - 0.7);
    if (length(uv - sun) < 0.1) {
        fragColor = vec4(1., 1. * light, 0., 1.0);
    }

    // moon
 	float pi = 3.14159265;
    float delta = 0.05;
    vec2 moon = vec2(cos(angle + pi) * 1.9, 1.5 * sin(angle + pi) - 0.7);
    vec2 moonmask = vec2(cos(angle + pi + delta) * 1.9, 1.5 * sin(angle + pi + delta) - 0.7);
    if (length(uv - moon) < 0.1 && length(uv - moonmask) > 0.13) {
        fragColor = vec4(1., 1., 1.0, 1.0);
    }


    // ground
    if (uv.y < -0.2) {
        fragColor = vec4(0.1, 0.5 * light + 0.2, 0., 1.0);
    }

}
