void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 xy = fragCoord.xy / iResolution.xy;

    vec4 col = texture(iChannel0, xy);
    col *= col;

    float luminosity = 0.299 * col.r +  0.587 * col.g + 0.114 * col.b;
    vec4 RGB = vec4(luminosity);

    vec4 sRGB = sqrt(RGB);

    fragColor = sRGB;
}
