// values for House
vec2 HouseLocation = vec2(0.5, 0.15);
float houseLength = 0.2;
float houseWidth = 0.15;

// values for Chimney
vec2 ChimneyLocation = vec2(0.33, 0.35);
float chimneyLength = 0.03;
float chimneyWidth = 0.05;

// values for Windows
vec2 Window1 = vec2(0.4, 0.2);
vec2 Window2 = vec2(0.6, 0.2);
float windowLength = 0.05;
float windowWidth = 0.05;

// colors
vec4 white = vec4(1.0, 1.0, 1.0, 1.0);
vec4 red = vec4(1.0, 0.0, 0.0, 1.0);
vec4 green = vec4(0.0, 1.0, 0.0, 1.0);
vec4 blue = vec4(0.5, 0.8, 1.0, 1.0);


// switches different layers on or off: 1.0 is on, 0.0 is off
float HouseOn = 1.0;
float ChimneyOn = 1.0;
float WindowOn = 1.0;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

 	vec2 uv = fragCoord.xy / iResolution.xy;

  	uv.y *= iResolution.y / iResolution.x;

    // layers
    vec4 BackGround = blue;
	vec4 House = red;
    vec4 Chimney = green;
    vec4 Windows = white;

   	// House Layer
    House *= step(length(uv.x - HouseLocation.x), houseLength) * 
        	 step(length(uv.y - HouseLocation.y), houseWidth);

    House *= HouseOn;

    // Chimney Layer
    Chimney *= step(length(uv.x - ChimneyLocation.x), chimneyLength) * 
        	   step(length(uv.y - ChimneyLocation.y), chimneyWidth) * ChimneyOn;

    // Windows
    Windows  *=  (step(length(uv.x - Window1.x), windowLength) * 
        		 step(length(uv.y - Window1.y), windowWidth) +
        		 step(length(uv.x - Window2.x), windowLength) * 
        		 step(length(uv.y - Window2.y), windowWidth)) * WindowOn;



    // mix layers on top of one another
    vec4 Composite = mix(BackGround, House, House.a);
    Composite = mix(Composite, Chimney, Chimney.a);
    Composite = mix(Composite, Windows, Windows.a);

    fragColor = Composite;
}
