#define JULIA_MODE
#define r(a) mat2(cos(a),sin(a),-sin(a),cos(a))

const float it=256.;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = 2.*fragCoord.xy /iResolution.y-vec2(iResolution.x/iResolution.y,1.);
    float a=0.;
    vec2 z = vec2(0.);
    #ifdef JULIA_MODE
    z=uv;
    uv=2.*iMouse.xy /iResolution.y-vec2(iResolution.x/iResolution.y,1.);
    if(iMouse.xy==vec2(0.))
        uv=vec2(.5)*r(iTime);
    #endif
    for(float i = 0.;i<it;i++)
    {
        a=i;
        z=z*mat2(z*vec2(1,-1),z.yx)+uv;
        z*=r(iTime/(i+1.));
        if(length(z)>2.)
            break;
    }
// a=length(z);
	fragColor = vec4(z,pow(a/it,.5),1.0);
}
