#define BLACK vec4(0., 0., 0., 1.)
#define YELLOW vec4(1., 1., 0., 1.)
#define DARK_GREEN vec4(0., .5, .0, 1.)
#define MAXDIST 30

vec4 pixelAt(sampler2D tex, vec2 screenCoord) {
    return texture(tex, screenCoord / iResolution.xy);
}

bool inProximity(vec2 xy, vec4 color, float variance, float dist, out vec2 separation) {
    if(dist > float(MAXDIST)) dist = float(MAXDIST);

    for(int i = -MAXDIST; i <= MAXDIST; i++) {
        if(abs(float(i)) > dist) continue;
        for(int j = -MAXDIST; j <= MAXDIST; j++) {
            if(abs(float(j)) > dist || length(vec2(i, j)) > dist) continue;
            if(length(distance(color, pixelAt(iChannel0, xy + vec2(i, j)))) < variance) {
                separation = vec2(iTime, 0);
                return true;
            }
        }
    }
    return false;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec4 color = pixelAt(iChannel0, fragCoord);
    vec2 separation;

    if(inProximity(fragCoord, BLACK, iTime / 10., iTime, separation)) {
        color *= DARK_GREEN * length(separation) / (iTime / 2.);
    }

    fragColor = color;
}
