float arg(vec2 z) {
    return atan(z.y, z.x);
}

float zig(vec2 p, float n, float r, float d) {
    // n = order, r = radius, d = extra length
 	float l = length(p);
    l += sin(n * abs(arg(p))) * d;
	return 1. - step(r, l) - step(0.04 - r, -l);

}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    float t = iTime;
    vec3 col;

    vec2 uv = 2. * fragCoord/iResolution.xy - 1.;
    uv /= 3.;
    uv *= 0.5 * vec2(sin(t / 17.), cos(t / 19.));

    float a = zig(uv, 160. * sin(t / 12.), 0.5 * sin(t / 17.), 0.48 * sin(t / 19.));
    float b = zig(uv, 128. * sin(t / 16.), 0.5 * sin(t / 13.), 0.2 * sin(t / 13.));
    float c = zig(uv, 256. * sin(t / 15.), 0.5 * sin(t / 11.), 0.4 * sin(t / 17.));
    float d = zig(uv, 64. * sin(t / 14.), 0.5 * sin(t / 19.), 0.2 * sin(t / 17.));

    col = vec3(a + b, b + c, c + a);
    col.r += d;
    float m = a + b + c + d;
    // vec3 base = sin(t / 4. * vec3(.345, .456, .567)) * .4 + .6; 
    // col = vec3(m); // * base;
    // col *= (1. + cos(iTime / 16.+uv.yyx+vec3(0,2,4))) / 2.;










    // Output to screen
    fragColor = vec4(col,1.0);
}
