float sin1(float x, float offset) {
	return (sin(x-offset)+1.0)/1.0;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = 2.0*(fragCoord-0.5*iResolution.xy)/iResolution.xy;
    vec2 uvD = fragCoord.xy/iResolution.xy;

    uv.x *= iResolution.x/iResolution.y;


    vec3 col = vec3(0.,0.,0.);
    float d = texture(iChannel0, vec2(((uvD.x+0.50)/4.0), 0.0)).x - 0.5;
    float g = texture(iChannel0, vec2(((uvD.x+0.51)/4.0), 0.0)).x - 0.5;

	float s = d; //sin(4.0*uv.x + iTime)* 0.5;
    float b = g;//sin(3.8*uv.x + iTime+0.15)* 0.5;

    float c0 = 0.009/abs(s-uv.y);
    float c1 = 0.005/abs(b-uv.y);
    float z = min(c0, c1);

    col = vec3(0.5+sin1(4.0*uv.x+iTime, 0.0), 
               0.5+sin1(4.0*uv.x+iTime, 3.14/2.0), 
               0.5+sin1(4.0*uv.x+iTime, 3.14/3.0)) * z;

    fragColor = vec4(col,1.0);
}
