float rep( float r )
{
    return sin(r) * cos(r) / tan(r) * sqrt(r); //<-- guess this's a polynomial.
}
//experiment rep if u want.
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    float col = fract(sin(dot(rep(length(uv)), tan(length(iTime))) + smoothstep(uv.y, fract(mod(uv.x, uv.x/2.0)), distance(length(uv), uv.y)))/length(uv));
	fragColor = vec4(col) / tan(fract(sin(col)));
    fragColor -= vec4(1.0, uv, 1.0);
}
