void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	fragColor = texture(iChannel0,uv);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    int frame = iFrame - iFrame / 200 * 200;
    if(frame == 0){
        fragColor = texture(iChannel0,fragCoord.xy / 256.);
    }else if(frame <= 136 && fragCoord.x < 256. && fragCoord.y < 256.){
        vec4 v = texture(iChannel2,vec2(0.25,0.5));
        int sz = 0;
        sz *= 128; sz += int(v.r * 128.);
        sz *= 128; sz += int(v.g * 128.);
        sz *= 128; sz += int(v.b * 128.);
        sz *= 128; sz += int(v.a * 128.);
        v = texture(iChannel2,vec2(0.75,0.5));
        int df = 0;
        df *= 128; df += int(v.r * 128.);
        df *= 128; df += int(v.g * 128.);
        df *= 128; df += int(v.b * 128.);
        df *= 128; df += int(v.a * 128.);

        int ix = int(fragCoord.x) + int(fragCoord.y)*256;
        int packi = ix/sz;
        int dn = packi-packi/2*2; // sort direction
        int pOrig = packi*sz;
        int comp = (ix-pOrig)/df; // reference point
        comp -= comp/2*2;
        int ay = comp==0?ix+df:ix-df; // compare target
        vec4 col1 = texture(iChannel1,uv);
        vec4 col2 = texture(iChannel1,vec2(float(ay-ay/256*256)+0.5,float(ay/256)+0.5)/iResolution.xy);
        int dir = length(col1) > length(col2) ? 0 : 1;
        int a = dir+comp+dn;
        a-=a/2*2;
        if(ay < 0 || ay >= 256*256){
	        fragColor = col1;
        }else{
	        fragColor = a==1 ? col1 : col2;
        }
    }else{
        fragColor =texture(iChannel1, uv);
    }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    int frame = iFrame - iFrame / 200 * 200;
    if(frame == 0){
         if(uv.x < 0.5)fragColor = vec4(0,0,0,1./128.);
         else fragColor = vec4(0,0,0,1./128.);
    }else{
        vec4 v = texture(iChannel0,vec2(0.25,0.5));
        int c = 0;
        c *= 128; c += int(v.r * 128.);
        c *= 128; c += int(v.g * 128.);
        c *= 128; c += int(v.b * 128.);
        c *= 128; c += int(v.a * 128.);
        v = texture(iChannel0,vec2(0.75,0.5));
        int d = 0;
        d *= 128; d += int(v.r * 128.);
        d *= 128; d += int(v.g * 128.);
        d *= 128; d += int(v.b * 128.);
        d *= 128; d += int(v.a * 128.);

        if(d==1)d=c,c*=2;
        else d/=2;

        if(uv.x < 0.5){
          fragColor.a = float(c-c/128*128)/128., c/=128;
          fragColor.b = float(c-c/128*128)/128., c/=128;
          fragColor.g = float(c-c/128*128)/128., c/=128;
          fragColor.r = float(c-c/128*128)/128., c/=128;
        }else{
          fragColor.a = float(d-d/128*128)/128., d/=128;
          fragColor.b = float(d-d/128*128)/128., d/=128;
          fragColor.g = float(d-d/128*128)/128., d/=128;
          fragColor.r = float(d-d/128*128)/128., d/=128;
        }
    }
}
