// lattice from https://www.shadertoy.com/view/Xl3GDS
// and https://www.shadertoy.com/view/XtcGDB

void mainImage( out vec4 O,  vec2 U )
{
    O-=O;
    for (float i=.1; i<1.01; i+=.1) {
        float v =  1.-cos(6.3*i), 
              r = .5+.5*sin(.3*iDate.w);
        O += smoothstep(-v,v, sin(60.*r*length( fract( U/i/70. - iDate.w* i ) -.5 ))-.8)
             * (i - O);                                    // color
          // * (.5+ .5* cos(6.3*i+vec4(0,-2,2,0)) - O);    // color
    }
}
