float isInside(vec2 p, vec4 bound){
    vec2 uva  = step( p,bound.xy+0.001);
    vec2 uvb  = step( bound.zw, p);
	return ( uva.x * uva.y * uvb.x * uvb.y );
}

vec4 inLogo(vec2 uv){
    float circle = (step(length(uv),0.95)); // outer
    float circle2 = (step(length(uv+vec2(0.0,0.21)),0.6));//bottom of FU
    float circle3 = (step(0.30, length(uv+vec2(0.0,0.21)))); // center of FU

    float Uup =  ( 
      isInside(uv,vec4(0.6,0.6,0.3,-0.231)) //U upright
    + isInside(uv,vec4(-0.3,0.6,-0.6001,-0.231)) //Fupright
    + isInside(uv,vec4(0.18,0.6,-0.6,0.3)) //Ftop
    + isInside(uv,vec4(0.18,0.17,-0.6,-0.13)) //FMid
    );

    float inside = (circle * ( step(-0.2, uv.y) + step(circle2 * circle3,0.0) ));

    return step(1.0,inside) * (step(Uup,0.0)) * vec4(1.0);   
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv-=0.5;
    uv*=2.0;
    uv.x*= iResolution.x/iResolution.y;
    vec2 res = 1.0/iResolution.xy;
    res.x*= iResolution.x/iResolution.y;
    vec4 samp;
    float div =0.0;
    const int t=2;

    for (int x =-t;x<t;x++){
    for (int y =-t;y<t;y++){
        vec2 offset = vec2(x,y) * res;
        samp += inLogo(uv+offset);
        div++;
    } 
    }
    samp/=div;

    fragColor=samp;

}
