float rect(vec2 pos, float maring) {
    vec2 bl = step(vec2(maring), pos);
    vec2 tr = step(vec2(maring), 1.-pos);
    return tr.x * tr.y * bl.x * bl.y;;

}

float circle(vec2 pos, float radius) {
    return smoothstep(radius,radius+0.778,distance(pos, vec2(-0.080,0.120)));
}
void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
   vec2 pos = fragCoord.xy/iResolution.xy;

    if (iResolution.y > iResolution.x ) {
        pos.y *= iResolution.y/iResolution.x;
        pos.y -= (iResolution.y*.5-iResolution.x*.5)/iResolution.x;


     } else {
        pos.x *= iResolution.x/iResolution.y;
        pos.x -= (iResolution.x*.5-iResolution.y*.5)/iResolution.y;
        }

    pos = fract(pos * 10.152);
    vec3 color = vec3(pos.x,0.569,1.000);
      pos += vec2(sin(iTime),0.060);
    float df = distance(pos, vec2(.5));

   color += sin(step(0.948,1.-df));

        fragColor = vec4(color,0.688);


}
