float Cross( const in vec2 A, const in vec2 B )
{
    return A.x * B.y - A.y * B.x;
}

vec2 GetUV(const in vec2 A, const in vec2 B, const in vec2 C, const in vec2 P)
{
    vec2 vPB = B - P;
    float f1 = Cross(A-B, vPB);
    float f2 = Cross(B-C, vPB);
    float f3 = Cross(C-A, C-P);

    return vec2(f1, f2) / (f1 + f2 + f3);
}

float InQuad( const in vec2 A, const in vec2 B, const in vec2 C, const in vec2 D, const in vec2 P )
{
	#ifdef CURVES_ONLY
	return 1.0;
	#endif

    float f1 = Cross(B-A, A-P);
    float f2 = Cross(C-B, B-P);
    float f3 = Cross(D-C, C-P);
    float f4 = Cross(A-D, D-P);

    return (max(max(max(f1, f2), f3), f4));
}


/* Letter A*/
float EuGlyph0(const in vec2 uv)
{
    const vec2 vP0 = vec2 ( 0.196, 0.031 );
    const vec2 vP1 = vec2 ( 0.228, 0.025 );
    const vec2 vP2 = vec2 ( 0.235, 0.170 );
    const vec2 vP3 = vec2 ( 0.197, 0.183 );

    return InQuad(vP0, vP1, vP2, vP3, uv);
}


/* Letter Z*/
float EuGlyph1(const in vec2 uv)
{
    const vec2 vP0 = vec2 ( 0.155, 0.035 );
    const vec2 vP1 = vec2 ( 0.240, 0.030 );
    const vec2 vP2 = vec2 ( 0.250, 0.065 );
    const vec2 vP3 = vec2 ( 0.157, 0.077 );

    const vec2 wP0 = vec2 ( 0.135, 0.145 );
    const vec2 wP1 = vec2 ( 0.250, 0.140 );
    const vec2 wP2 = vec2 ( 0.245, 0.180 );
    const vec2 wP3 = vec2 ( 0.140, 0.185 );

    float fDist = 1.0;
    fDist = min( fDist, InQuad(vP0, vP1, vP2, vP3, uv) );
    fDist = min( fDist, InQuad(wP0, wP1, wP2, wP3, uv) );    
    fDist = min( fDist, InQuad(vP1, vP2, wP3, wP0, uv) );

    return fDist;
}

/* Letter I*/
float EuGlyph2(const in vec2 uv)
{
    const vec2 vP0 = vec2 ( 0.151, 0.028 );
    const vec2 vP1 = vec2 ( 0.190, 0.020 );
    const vec2 vP2 = vec2 ( 0.175, 0.180 );
    const vec2 vP3 = vec2 ( 0.137, 0.185 );

    return InQuad(vP0, vP1, vP2, vP3, uv);
}

/* Letter N*/
float EuGlyph3(const in vec2 uv)
{
    const vec2 vP0 = vec2 ( 0.151, 0.025 );
    const vec2 vP1 = vec2 ( 0.185, 0.023 );
    const vec2 vP2 = vec2 ( 0.175, 0.180 );
    const vec2 vP3 = vec2 ( 0.137, 0.185 );

    const vec2 wP2 = vec2 ( 0.235, 0.180 );
    const vec2 wP3 = vec2 ( 0.200, 0.185 );

    float fDist = 1.0;
    fDist = min( fDist, InQuad(vP0, vP1, vP2, vP3, uv) );
    fDist = min( fDist, InQuad(vP0, vP1, wP2, wP3, uv) );

    return fDist;
}

/* Letter G*/
float EuGlyph4(const in vec2 uv)
{
    vec2 vP = uv - vec2(0.56, 0.1);
    vP /= 0.08;
    vP.x *= 1.4;
    vP.x += vP.y * 0.25;

    vec2 vP2 = vP;

    vP.y = abs(vP.y);
    vP.y = pow(vP.y, 1.2);
    float f= length(vP);

    vP2.x *= 1.5;
    float f2 = length(vP2 * 1.6 - vec2(0.2, 0.2));

    float fDist = 1.0;
    fDist = max(f - 1.0, 1.0 - f2) / 20.0;


    const vec2 vvP0 = vec2 ( 0.58, 0.07 );
    const vec2 vvP1 = vec2 ( 0.62, 0.03 );
    const vec2 vvP2 = vec2 ( 0.62, 0.10 );
    const vec2 vvP3 = vec2 ( 0.58, 0.10 );    
    float sub = InQuad(vvP0, vvP1, vvP2, vvP3, uv);

    fDist = max(fDist,-sub);
    return fDist;
}

/* Horizontal stuff */
float EuGlyph5(const in vec2 uv)
{
    const vec2 wP0 = vec2 ( 0.56, 0.10 );
    const vec2 wP1 = vec2 ( 0.60, 0.10 );
    const vec2 wP2 = vec2 ( 0.60, 0.125 );
    const vec2 wP3 = vec2 ( 0.55, 0.13 );
    return InQuad(wP0, wP1, wP2, wP3, uv);
}

float circle(const in vec2 uv)
{
    vec2 vP = uv;
    float f= length(vP);
    return f-1.0;
}

float BAZINGA(in vec2 uv)
{
    float fResult = 1.0;

    // Bend a little bit
    uv.x-=sin(uv.y*15.0)*0.01;

    // Right stick of Letter A
    vec2 vUVOffset3 = vec2(0.0, 0.0);
    vec2 vTailOffset3 = vec2(0.0, 0.0);
    if(uv.x > 0.6)
    {
        vUVOffset3 = vec2(-0.47, 0.0);  
        vTailOffset3 = vec2(-0.01, 0.1);   
    }
    fResult = min(fResult, EuGlyph0(uv + vUVOffset3));

    vec2 vUVOffset3_1   = vec2(-0.1, 0.0);
    fResult = min(fResult, EuGlyph1(uv + vUVOffset3_1));

    // Vertical of B
    vec2 vUVOffset3_2   = vec2(0.092, -0.009);
    if(uv.x > 0.70) {
        // Vertical of esc.Mark
		vUVOffset3_2   = vec2(-0.59, 0.03);
    } else if(uv.x > 0.6) {
        // Vertical of Second A
		vUVOffset3_2   = vec2(-0.48, -0.01);    
    } else if(uv.x > 0.4) {
        // Vertical N
		vUVOffset3_2   = vec2(-0.315, 0.0);    
    } else if(uv.x > 0.3) {
        // I
		vUVOffset3_2   = vec2(-0.21, 0.0);    
    } else if(uv.x > 0.12) {
        // Vertical of First A
		vUVOffset3_2   = vec2(-0.01, -0.01);
    }

    fResult = min(fResult, EuGlyph2(uv + vUVOffset3_2));

    vec2 vUVOffset3_3   = vec2(-0.255, 0.0);
    fResult = min(fResult, EuGlyph3(uv + vUVOffset3_3)); 

    vec2 vUVOffset3_4   = vec2(-0.0, 0.0);
    vec2 flip = vec2(1.0,1.0);
    if(uv.x > 0.06 && uv.x < 0.2 ){
        if (uv.y > 0.111){
            //Bottom Curved B
            vUVOffset3_4   = vec2(0.65, 0.4);
            flip = vec2(-1.0,-2.0);
        }else{
            //Top Curved B
            vUVOffset3_4   = vec2(0.66, 0.2);
            flip = vec2(-1.0,-1.5);
        }
    }

    fResult = min(fResult, EuGlyph4(flip*uv + vUVOffset3_4)); 

    // Horizontal of First A
    vec2 vUVOffset3_4_1   = vec2(0.4, -0.03);
    if(uv.x > 0.6)
    {
        // Horizontal of Second A
		vUVOffset3_4_1   = vec2(-0.1, -0.03);
    } else if(uv.x > 0.5)
    {
        // Horizontal G
		vUVOffset3_4_1   = vec2(-0.0, 0.0);    
    }    
    fResult = min(fResult, EuGlyph5(uv + vUVOffset3_4_1));

    vec2 vUVOffset3_6  = vec2(-26.0, -7.0);
    float vUVSize3_6   = 35.0;
    fResult = min(fResult, circle(uv*vUVSize3_6 + vUVOffset3_6)); 

    return fResult; 
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{	
    float overlap    = 100.0;
    float win        = 4096.0;
    float winMin     = win - overlap;
    float chunks     = 9.0;
    float sampleRate = 48000.0;

    float winTime    = win / sampleRate ;
    float winMinTime = winMin / sampleRate ;
    float totalTime  = chunks * winMin / sampleRate ;

    // Add some delay between any sample
    float bTime = mod(iTime+totalTime*1.0,totalTime*3.0);
    if (bTime > totalTime)bTime=0.0;

    float chunkTime = floor((iTime+totalTime*1.5)/(totalTime*3.0));

	vec4 vResult = vec4(0.0);

	vec2 vUV = fragCoord.xy / iResolution.xy;
	vUV.x = ((vUV.x - 0.5) * (iResolution.x / iResolution.y)) + 0.5;    
	vUV.y = 1.0 - vUV.y;

	vec2 vFontUV = vUV * 0.7;
	vec2 vBgUV = vUV;

    vFontUV.x += 0.1 + 1.0-bTime*3.0;
    vFontUV.y -= 0.25 + 0.2*sin(4.56*chunkTime);


	float fBAZINGA  = step(BAZINGA(vFontUV), 0.0);
    float f1BAZINGA = step(BAZINGA(vFontUV-0.01), 0.0);

    float circlebg = step(circle(3.0*(vUV+vec2(-0.5))), 0.0);

    vec3 bgcol  = mix (vec3(0.8,0.0,0.0),vec3(1.0),circlebg);
    vec3 shadow = mix(bgcol,vec3(0),f1BAZINGA);	
    fragColor = vec4(mix(shadow,vec3(0.8,0.8,0.0),fBAZINGA),1.0);
}
