float animSpeed = 10.0;

void mainImage( out vec4 color, in vec2 fragCoord )
{
    color = vec4(0,0,0,1);
    float size = 10.0;
    float intensity = cos((fragCoord.x+fragCoord.y)*0.01+iTime*animSpeed)*tan(fragCoord.x*fragCoord.y)*0.8;
    if(sin(fragCoord.y/size) < mod(sin(fragCoord.x/size+3.14*iTime),cos(iTime))){
        color = vec4(intensity,0,0,1);
    }else if(-sqrt(cos(fragCoord.y/size-3.14*iTime)) < sin(fragCoord.x/size)){
        color = vec4(0,intensity,0,1);
    }
    else if(cos(fragCoord.y/size+3.14*iTime) < cos(fragCoord.x/size)){
        color = vec4(0,0,intensity,1);
    }
}
