void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    vec3 colors = vec3(0.0);

    float grid = 10.0;
    float ratio = 1.0/grid;

    vec2 uv2 = uv * grid;
    vec2 uv3 = fract(uv2) * ratio;

    if(uv2.y < 1.0 && uv2.x < 1.0){

      uv = fract(uv2);
      colors = texture(iChannel0,uv).rgb;

    } else {
      uv = uv3 + (ratio * max(floor(uv2 - 1.), 0.0));
      colors = texture(iChannel1, uv).rgb;   
    }
    fragColor = vec4(colors,1.0);

}
