int N = 48; // draw NxN points of coords ( texture(i).x, texture(i).y )

// point distribution
#define f0(c) max(0.,1.-100.*length( U - texelFetch(c,ivec2(i%N,i/N),0).xy ))
// histogram
#define f1(c) float( texelFetch(c,ivec2(i%N,i/N),0).x < U.y ) / float(N*N)

#define f(c) f0(c) + vec4(f1(c),0,0,0)

void mainImage( out vec4 O, in vec2 U )
{
    vec2 R = iResolution.xy;
    O-=O;
    O.g = float(U.x-.5==R.x/2.);                      // separator
    U /= R.y;

    if (U.x < .5*R.x/R.y)
        for(int i=0; i<N*N; i++)  O += f(iChannel0);  // blue noise
    else { 
        U.x -= .5*R.x/R.y;
        for(int i=0; i<N*N; i++)  O += f(iChannel1);  // white noise
       }
}
