void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord/iResolution.xy;

    float balanco=sin(iTime)*0.01+sin(uv.x*3.0+iTime)*0.01;

    if(uv.y<(0.66+balanco))
        fragColor=vec4(0,0,0,0);
    if(uv.y<(0.67+balanco) && uv.y>(0.32+balanco))
        fragColor=vec4(1,0,0,0);
    if(uv.y<(0.33+balanco))
        fragColor=vec4(1,1,0,0);
    if(uv.y<(0.01+balanco))
        fragColor=vec4(1,0,1,0);
    if(uv.y>(0.98+balanco))
        fragColor=vec4(1,0,1,0);



}
