void mainImage( out vec4 fragColor, in vec2 fragCoord )
{


    vec2 st = fragCoord.xy/iResolution.xy;
    st.x *= iResolution.x/iResolution.y;
	st.x -= 0.4;

    vec3 color = vec3(0.);

    float f;

    st -=0.5;
    float r = length(st);

    f = distance(vec2(0.), st.xy);


    f = sin(atan(st.x, st.y)*sin(iTime)*8000. +30.);
    f = smoothstep(f, f-.8, r);

    color = vec3(f);

    fragColor = vec4(color,1.0);
}
