// see also https://www.shadertoy.com/view/MlcGD7

#define RGB(f) ( .5 + .5*cos(f + vec4(2.1,0,-2.1, 1)) ) // phases -2pi/3, 0, +2pi/3 -> sum = 1
void mainImage( out vec4 O,  vec2 U )
{
    U /= iResolution.xy;

    // decompose a pure frequency color into R,G,B using kernel (1+cos)/2
    vec4  T = RGB(iTime);
    // T /= max(T.r,max(T.g,T.b)); // chrominance only

    float z = 4.*U.x;

    O =  pow(.8*T, vec4(z));  
}
