const float y_dist = 0.5;

float c_r;
vec2 pos;

// nx = x / z
// ny = y / z
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    c_r = mod(iTime * 0.5, 2.0 * 3.1415);
    pos.x = cos(c_r);
    pos.y = sin(c_r);
    vec2 ns = vec2((fragCoord.x / iResolution.x - 0.5) * 2.0, (fragCoord.y / iResolution.y - 0.5) * 2.0);
    float z = abs(y_dist / ns.y);
    float x = ns.x * z;
    float rx = x * cos(c_r) - z * sin(c_r) + pos.x;
    float rz = x * sin(c_r) + z * cos(c_r) + pos.y;
    vec4 color = vec4(0.0);
    if (z < 100.0) {
        vec2 tex = vec2(mod(rx, 1.0), mod(rz, 1.0));
        color = texture(iChannel0, tex);
        color.xyz /= z * 2.0;
    }
	fragColor = color;
}
