void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	vec2 staticUv = uv;    
    uv.x -= (iTime) / 2.0;
    uv.x *= 0.2;
    uv *= 5.0;

	fragColor = texture(iChannel1, staticUv);

    float offset = sin(iTime);
    //offset = 0.0;
    uv.y += offset;


    vec4 cat = texture(iChannel0, uv);
    if (cat.a > 0.9)
    {

        if (fract(uv.x) < 0.15 && 
            fract(staticUv.y) < 0.6 && fract(staticUv.y) > 0.4)
        {
		    cat = texture(iChannel0, uv);
	    	fragColor = cat;
        }    
    }

    //fragColor = vec4(uv.x, uv.y, 0.0, 1.0);
}
