void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    float rot = radians(iTime * 45.0);

    uv += ((uv/1000.0) + cos(iTime));

    mat2 m = mat2(cos(rot), -sin(rot), sin(rot), cos(rot));

    uv  = m * uv;

    vec4 texture1Color = texture(iChannel0, uv);
    vec4 texture2Color = texture(iChannel1, uv);
    vec4 texture3Color = texture(iChannel2, uv);
    vec4 texture4Color = texture(iChannel3, uv);

    float lerp = sin(iTime) * 0.5 + 0.5;

    fragColor = texture1Color * lerp + (1.0 - lerp) * texture2Color + (1.0 - lerp) * texture3Color;
}
