// Author: blackpolygon
// Title:  Stars

// Based on 'Audio Eclipse' by airtight
// https://www.shadertoy.com/view/MdsXWM


const float dots = 250.; //number of lights
float radius = 0.050; //radius of light ring
const float brightness = 0.001;

//convert HSV to RGB
vec3 hsv2rgb(vec3 c){
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

mat2 rotate2d(float _angle){
    return mat2(cos(_angle),-sin(_angle),
                sin(_angle),cos(_angle));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 st = fragCoord.xy/min(iResolution.x,iResolution.y);
    st.x -= 0.35;

    st -= 0.5;
    vec3 color=vec3(0.0); //background color

    for(float i=0.;i<dots; i++){

        radius += (0.5+sin(iTime/3.)*0.3)*0.002;
        float b1a0 = 0.5+sin(iTime/5.)*0.5;
        float b1a02 = 0.5+cos(iTime/5.)*0.5;

        float x = radius*cos(2.*3.14*float(i)/(dots/(50.+b1a0*5.)));
        float y = radius*sin(2.*3.14*float(i)/(dots/(5. +b1a02*5.)));
        vec2 position = vec2(x,y);

		vec3 dotCol = vec3(0.5+sin(iTime)*0.2,0.20,0.734);

		color += (brightness/(length(st-position))*dotCol) * 0.8;
    }

	fragColor = vec4(color,1);
}
