#define RADIUS .05
#define RED vec4(.6, .09, .08, 1.)
#define GREEN vec4(.71, .79, .73, 1.)
#define BLUE vec4(.11, .15, .2, 1.)
#define WHITE vec4(1., .98, .9, 1.)
#define AARED aalias(RADIUS, dist, BLUE, RED, .008)

vec4 aalias(float radius, float dist, vec4 bg, vec4 color, float diff){
    return mix(bg, color, smoothstep(radius, radius - diff, dist));
}

float randSin(float x, float async) {
    return .1 * sin(10. * cos(x / 2.) + cos((12. + sin(iTime * async * .5)) * x) / 4. - sin(-iTime * 3. -28. * x) / 9.) - .3 + sin(iTime * .2 - cos(iTime * .6) * .2) * .08 + .25;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	vec2 lM = vec2(max(iResolution.x, iResolution.y), min(iResolution.x, iResolution.y));
	vec2 pos = (fragCoord -.5 * lM ) / lM.x;

    float dist = length(pos - vec2(.25, .15));
    pos.x *= 1.2;
    vec3 val = vec3(randSin(pos.x + iTime * .3, .01), randSin(pos.x + 10. + iTime * .6, .006), randSin(pos.x + 5. + iTime * 1.2, .009));

    if (pos.y < val.z) fragColor = aalias(val.z, pos.y, aalias(val.y, pos.y - .1, aalias(val.x, pos.y - .2, AARED, WHITE, .04), GREEN, .025), WHITE, .01);
    else {
        pos.y -= .1;
        if (pos.y < val.y) fragColor = aalias(val.y, pos.y, aalias(val.x, pos.y - .1, AARED, WHITE, .04), GREEN, .025);
        else {
            pos.y -= .1;
            if (pos.y < val.x) fragColor = aalias(val.x, pos.y, AARED, WHITE, .04);
            else {
                if (dist < RADIUS) fragColor = AARED;
                else fragColor = BLUE;
            }
        }
    }
}
