#define R iResolution.xy
#define t iTime
#define f(i) 2. * cos(i.y + acos(cos(2. * i.x)) / (3.)) * sin(i.x) - 2.5 * sin(t)
void mainImage(out vec4 C, in vec2 U)
{
    C = (vec4(1., .5, .5, 1.) + float(abs(f((U / R.y * 20. - vec2(t)))) < 1.)) / sqrt(.65 - .9 * length(.55 * (U / R - .5)));
}
