#define RADIUS .15
#define RED vec4(.6, .09, .08, 1.)
#define GREEN vec4(.71, .79, .73, 1.)
#define BLUE vec4(.11, .15, .2, 1.)
#define WHITE vec4(1., .98, .9, 1.)

vec4 aalias(float radius, float dist, vec4 bg, vec4 color, float ratio){
    return mix(bg, color, smoothstep(radius, radius * ratio, dist));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 lM = vec2(max(iResolution.x, iResolution.y), min(iResolution.x, iResolution.y));
    vec2 pos = (fragCoord -.5 * lM) / lM.x;

    if (pos.y < -abs(pos.x)) {
        float x = pos.x;
        bool shadow = pos.y > -x * 1.8;

        x = .008 * floor(sin(.5 + x - cos(x * 19.) * 7. + sin(x * 13.) * 3.) * 4.) / 4. - .08;
        x = mix(x, .08 * abs(pos.x * 15. - ceil(pos.x * 15.) + .5) - .12, .5);

        if (pos.y > x) {
            if (shadow) fragColor = mix(BLUE, WHITE, .5);
            else fragColor = WHITE;
        }
        else if (shadow) fragColor = mix(BLUE, GREEN, .5);
        else fragColor = GREEN;
    }
    else {
    	float dist = length(pos);
        if (dist < RADIUS) fragColor = aalias(RADIUS, dist, BLUE, RED, .97);
    	else fragColor = BLUE;
    }
}
