vec2 fragCoord;
vec4 fragColor;
void dimCircle( vec2 center, float radius, float ignore_radius )
{
    float dist = abs(distance(center.xy, fragCoord.xy));
    if( dist <= radius && dist >= ignore_radius ) {
        float m = dist/radius;

        fragColor /= vec4(m,m,m,1.0);
        clamp(fragColor.r, 0.0, 1.0);
        clamp(fragColor.g, 0.0, 1.0);
        clamp(fragColor.b, 0.0, 1.0);
    }    	

}

void drawBubble( vec2 center, float radius )
{
    float dist = abs(distance(center.xy, fragCoord.xy));
    if( dist<=radius ) {
    	fragColor.r = dist/radius;

        fragColor.g *= 1.1;
        clamp(fragColor.g, 0.0, 1.0);
        fragColor.b *= 1.1;
        clamp(fragColor.b, 0.0, 1.0);
    }
}

void mainImage( out vec4 oFragColor, in vec2 iFragCoord )
{
    fragCoord = iFragCoord;
	vec2 uv = fragCoord.xy / iResolution.xy;
	fragColor = vec4(uv,0.5+0.5*sin(iTime),1.0);

    vec2 pos = iMouse.xy;
    float rad_main = 20.0;
    float rad_outer = 30.0;

    dimCircle( pos, rad_outer, rad_main );
    drawBubble( pos, rad_main );
    drawBubble( pos, 100.0 * cos(iTime) );
    oFragColor = fragColor;
}
