void mainImage(out vec4 O, vec2 u) {
 	vec2 R = iResolution.xy,
         U = (u+u-R)/R.y /.7;
         U.x = asin( U.x / cos( U.y = asin(U.y) )) - iTime;
         U = 6.* sin(8.*U);
         O += U.x/U.x -O;
         O.gb += U.x*U.y; }


