float s( float b ) {
    return abs( b / 2. - mod(  iTime * 9., b ) );
}
void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	vec4 p = vec4(fragCoord,0.,1.);
    p.z = .5;
	vec3 d = normalize( p.xyw / 9e2 -.5 ),    
	     c = vec3( s(39.), s(17.), s(71.) );
	float t = dot( c, d ), 
		  e = length( c - t * d );    
	vec3 i = t * d - sqrt( 1. - e * e );
	fragColor = e < 1. ? dot( i - c, normalize( vec3( 9. ) - i ) ) * p.wzzw : p.zzzw;
}
