#define resolution vec2(500.0, 500.0)
#define Thickness 0.002

float drawLine(vec2 uv, vec2 p1, vec2 p2, vec2 ctrl1, vec2 ctrl2) {
    float d1 = distance(uv, ctrl1);
    uv.y -= (d1 * 0.5);

    float d2 = distance(uv, ctrl2);
    uv.y += (d2 * 0.5);

  	float a = abs(distance(p1, uv));
  	float b = abs(distance(p2, uv));
  	float c = abs(distance(p1, p2));

  	if ( a >= c || b >= c ) return 0.0;

  	float p = (a + b + c) * 0.5;

  	// median to (p1, p2) vector
  	float h = 2.0 / c * sqrt( p * ( p - a) * ( p - b) * ( p - c));

  	return mix(1.0, 0.0, smoothstep(0.5 * Thickness, 1.5 * Thickness, h));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    fragColor = vec4(drawLine(uv, vec2(0.0, 0.5), vec2(1.0, 0.5), vec2(0.4, 0.7), vec2(0.6, 0.2)));
}
