void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 pos = (fragCoord.xy * 2.0 - iResolution.xy) / iResolution.y;
    float color = 0.0;
	float s = 1.0;

    for(int i=0; i<10; ++i) {
        float fi = float(i);
		float t = atan(pos.x, pos.y) + iTime * 0.02 * fi;
        float len = (0.0002 * fi) / abs(length(pos) - (0.5 + sin(t * (3.0 + fi)) * (0.2 - float(i) * sin(iTime + fi) * 0.01)));
		color += len * (1.0 - fi / 20.0);
    }

    color += length(pos) * 0.1;

    fragColor = vec4(color) * vec4(2, 2, 4, 1);
}
